function Agreed() {
        if (document.getElementById('agree').disabled == true) {
                document.getElementById('agree').disabled = false;

        } else {
                document.getElementById('agree').disabled = true
        }
};

function changeSearch() {
        var changeBox = document.getElementsByClassName("searchField");

        if(changeBox[0].name == "SearchableText"){
                changeBox[0].name = "DocID";
                changeBox[0].placeholder = "Append wildcard to Document ID";
        } else {
                changeBox[0].name = "SearchableText";
                changeBox[0].placeholder = "Full Text Search";
        }
};


function changeSearchAlt() {
        var changeBox = document.forms["nolivesearchGadget_form"]["nolivesearchGadget"]

        if(isNaN(changeBox.value.substring(0,2))){
                changeBox.name = "SearchableText";
        } else {
                changeBox.name = "DocID";
        }
};

